import React from 'react';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import styled from 'styled-components';
// import YourBoardCards from 'features/yourBoard/YourBoardCards';

const YourBoard = props => {
  const { boardHeight, boardWidth } = useSelector(s => s.layout);
  return boardHeight && boardWidth ? (
    <div
      boardHeight={boardHeight}
      boardWidth={boardWidth}
      data-file="YourBoard"
    >
      {/* <YourBoardCards /> */}
      <style jsx>{`
        div {
          align-items: center;
          background: #111;
          box-shadow: inset 0 0 10px transparent;
          box-sizing: border-box;
          display: flex;
          flex-flow: row nowrap;
          height: ${`calc(${props.boardHeight}px / 2)`};
          justify-content: center;
          margin: auto auto 0;
          overflow: hidden;
          width: ${`${props.boardWidth}px`};
        }
      `}</style>
    </div>
  ) : null;
};

const Component = styled.div`
  align-items: center;
  background: #111;
  box-shadow: inset 0 0 10px transparent;
  box-sizing: border-box;
  display: flex;
  flex-flow: row nowrap;
  height: ${p => (p.boardHeight ? `calc(${p.boardHeight}px / 2)` : `0px`)};
  justify-content: center;
  margin: auto auto 0;
  overflow: hidden;
  width: ${p => (p.boardWidth ? `${p.boardWidth}px` : `0px`)};
`;

YourBoard.propTypes = {
  children: PropTypes.node
};

export default YourBoard;
