import React from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';
// import Card from 'components/Card/Card.component';
// import CardDragLayer from 'systems/CardDragLayer';

const YourHand = () => {
  // const yourCards = useSelector(s => s.yourHand);
  const yourCards = [];
  const { length } = yourCards;

  return (
    <Component
      data-layout="YourHand"
      data-length={length}
      numberOfCardsInHand={length}
    >
      {yourCards.map((card, index) => {
        return <div></div>;
      })}
    </Component>
  );
};

const Component = styled.div`
  display: flex;
  flex-flow: row nowrap;
  align-items: center;
  justify-content: center;

  & > div {
    position: relative;
    top: -20px;
  }

  & > div + div {
    margin-left: calc(calc(var(--card-height) / 1.4) / -3.5);
  }
`;

export default YourHand;
